// React Native SVG
declare module '*.svg' {
  import * as React from 'react';
  import { SvgProps } from 'react-native-svg';
  const content: React.FC<SvgProps>;
  export default content;
}

// React native feather icon
declare module 'react-native-vector-icons/Feather';

